INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (1, N'Brandy Alexander', N'Shake all ingredients with ice and strain contents into a cocktail glass. Sprinkle nutmeg on top and serve.', N'brandy_alexander.jpg', N'https://en.wikipedia.org/wiki/Alexander_(cocktail)')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (2, N'Americano', N'Pour the Campari and vermouth over ice into glass, add a splash of soda water and garnish with half orange slice.', N'americano.jpg', N'https://en.wikipedia.org/wiki/Americano_(cocktail)')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (3, N'Angel Face', N'Shake all ingredients with ice and strain contents into a cocktail glass.', NULL, N'https://en.wikipedia.org/wiki/Angel_Face_(cocktail)')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (4, N'Aviation', N'Add all ingredients into cocktail shaker filled with ice. Shake well and strain into cocktail glass. Garnish with a cherry.', N'aviation.jpg', N'https://en.wikipedia.org/wiki/Aviation_(cocktail)')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (5, N'Bacardi cocktail', N'Shake together with ice. Strain into glass and serve', N'bacardi_cocktail.jpg', N'https://en.wikipedia.org/wiki/Bacardi_cocktail')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (6, N'Between the Sheets', N'Pour all ingredients into shaker with ice cubes, shake, strain into chilled cocktail glass.', N'between_the_sheets.jpg', N'https://en.wikipedia.org/wiki/Between_the_Sheets_(cocktail)')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (7, N'Casino', N'Pour all ingredients into shaker with ice cubes. Shake well. Strain into chilled cocktail glass. Garnish with a lemon twist and a maraschino cherry. Serve without a straw.', NULL, N'https://en.wikipedia.org/wiki/Casino_(cocktail)')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (8, N'Clover Club Cocktail', N'Dry shake ingredients to emulsify, add ice, shake and served straight up.', N'clover_club_cocktail.jpg', N'https://en.wikipedia.org/wiki/Clover_Club_Cocktail')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (9, N'Daiquiri', N'Pour all ingredients into shaker with ice cubes. Shake well. Strain in chilled cocktail glass.', N'daiquiri.jpg', N'https://en.wikipedia.org/wiki/Daiquiri')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (10, N'Derby', N'Pour all ingredients into a mixing glass with ice. Stir. Strain into a cocktail glass. Garnish with a sprig of fresh mint in the drink.', NULL, N'https://en.wikipedia.org/wiki/Derby_(cocktail)')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (11, N'Manhattan', N'Stirred over ice, strained into a chilled glass, garnished, and served up.', N'manhattan.jpg', N'https://en.wikipedia.org/wiki/Manhattan_(cocktail)')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (12, N'Mary Pickford', N'Shake and strain into a chilled large cocktail glass', N'mary_pickford.jpg', N'https://en.wikipedia.org/wiki/Mary_Pickford_(cocktail)')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (13, N'Monkey Gland', N'Shake well over ice cubes in a shaker, strain into a chilled cocktail glass.', N'monkey_gland.jpg', N'https://en.wikipedia.org/wiki/Monkey_Gland')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (14, N'Negroni', N'Stir into glass over ice, garnish and serve.', N'negroni.jpg', N'https://en.wikipedia.org/wiki/Negroni')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (15, N'Old Fashioned', N'Place sugar cube in old fashioned glass and saturate with bitters, add a dash of plain water.
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (16, N'Paradise', N'Shake together over ice. Strain into cocktail glass and serve chilled.', N'paradise.jpg', N'https://en.wikipedia.org/wiki/Paradise_(cocktail)')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (17, N'Planter''s Punch', N'Pour all ingredients, except the bitters, into shaker filled with ice. Shake well. Pour into large glass, filled with ice. Add Angostura bitters, "on top". Garnish with cocktail cherry and pineapple.', N'planters_punch.jpg', N'https://en.wikipedia.org/wiki/Planter''s_Punch')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (18, N'Porto Flip', N'Shake ingredients together in a mixer with ice. Strain into glass, garnish and serve', N'porto_flip.jpg', N'https://en.wikipedia.org/wiki/Porto_flip')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (19, N'Rusty Nail', N'Pour all ingredients directly into old-fashioned glass filled with ice. Stir gently. Garnish with a lemon twist. Serve.', NULL, N'https://en.wikipedia.org/wiki/Rusty_Nail_(cocktail)')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (20, N'Sazerac', N'Rinse a chilled old-fashioned glass with the absinthe, add crushed ice, and set it aside.
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (21, N'Screwdriver', N'Mix in a highball glass with ice. Garnish and serve.', N'screwdriver.jpg', N'https://en.wikipedia.org/wiki/Screwdriver_(cocktail)')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (22, N'Sidecar', N'Pour all ingredients into cocktail shaker filled with ice. Shake well and strain into cocktail glass.', N'sidecar.jpg', N'https://en.wikipedia.org/wiki/Sidecar_(cocktail)')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (23, N'Stinger', N'Pour in a mixing glass with ice, stir and strain into a cocktail glass. May also be served on rocks in a rocks glass.', N'stinger.jpg', N'https://en.wikipedia.org/wiki/Stinger_(cocktail)')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (24, N'Tom Collins', N'Mix the gin, lemon juice and sugar syrup in a tall glass with ice, top up with soda water, garnish and serve.', N'tom_collins.jpg', N'https://en.wikipedia.org/wiki/Tom_Collins_(cocktail)')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (25, N'Whiskey sour', N'Shake with ice. Strain into chilled glass, garnish and serve.', N'whiskey_sour.jpg', N'https://en.wikipedia.org/wiki/Whiskey_sour')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (26, N'Bellini', N'Pour peach purée into chilled flute, add sparkling wine. Stir gently.', N'bellini.jpg', N'https://en.wikipedia.org/wiki/Bellini_(cocktail)')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (27, N'Black Russian', N'Pour the ingredients into an old fashioned glass filled with ice cubes. Stir gently.', N'black_russian.jpg', N'https://en.wikipedia.org/wiki/Black_Russian')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (28, N'Bloody Mary', N'Stirring gently, pour all ingredients into highball glass. Garnish.', N'bloody_mary.jpg', N'https://en.wikipedia.org/wiki/Bloody_Mary_(cocktail)')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (29, N'Caipirinha', N'Place lime and sugar into old fashioned glass and muddle (mash the two ingredients together using a muddler or a wooden spoon). Fill the glass with crushed ice and add the Cachaça.', N'caipirinha.jpg', N'https://en.wikipedia.org/wiki/Caipirinha')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (30, N'Champagne cocktail', N'Add dash of Angostura bitter onto sugar cube and drop it into champagne flute. Add cognac followed by gently pouring chilled champagne. Garnish with orange slice and maraschino cherry.', NULL, N'https://en.wikipedia.org/wiki/Champagne_cocktail')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (31, N'Cosmopolitan', N'Add all ingredients into cocktail shaker filled with ice. Shake well and double strain into large cocktail glass. Garnish with lime wheel.', N'cosmopolitan.jpg', N'https://en.wikipedia.org/wiki/Cosmopolitan_(cocktail)')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (32, N'Cuba Libre', N'Build all ingredients in a Collins glass filled with ice. Garnish with lime wedge.', N'cuba_libre.jpg', N'https://en.wikipedia.org/wiki/Cuba_Libre')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (33, N'French Connection', N'Pour all ingredients directly into old fashioned glass filled with ice cubes. Stir gently.', NULL, N'https://en.wikipedia.org/wiki/French_Connection_(cocktail)')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (34, N'French 75', N'Combine gin, syrup, and lemon juice in a cocktail shaker filled with ice. Shake vigorously and strain into a chilled champagne glass. Top up with Champagne. Stir gently.', N'french_75.jpg', N'https://en.wikipedia.org/wiki/French_75_(cocktail)')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (35, N'The Godfather', N'Pour all ingredients directly into old fashioned glass filled with ice cubes. Stir gently.', N'the_godfather.jpg', N'https://en.wikipedia.org/wiki/Godfather_(cocktail)')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (36, N'The Godmother', N'Pour all ingredients directly into old fashioned glass filled with ice cubes. Stir gently.', NULL, N'https://en.wikipedia.org/wiki/Godfather_(cocktail)')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (37, N'Golden Dream', N'Shake with cracked ice. Strain into glass and serve.', N'golden_dream.jpg', N'https://en.wikipedia.org/wiki/Golden_dream_(cocktail)')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (38, N'Grasshopper', N'Pour ingredients into a cocktail shaker with ice. Shake briskly and then strain into a chilled cocktail glass.', N'grasshopper.jpg', N'https://en.wikipedia.org/wiki/Grasshopper_(cocktail)')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (39, N'Harvey Wallbanger', N'Stir the vodka and orange juice with ice in the glass, then float the Galliano on top. Garnish and serve.', N'harvey_wallbanger.jpg', N'https://en.wikipedia.org/wiki/Harvey_Wallbanger')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (40, N'Horse''s Neck', N'Pour brandy and ginger ale directly into highball glass with ice cubes. Stir gently. Garnish with lemon zest. If desired, add dashes of Angostura Bitter.', N'horses_neck.jpg', N'https://en.wikipedia.org/wiki/Horse''s_Neck')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (41, N'Irish Coffee', N'Heat the coffee, whiskey and sugar; do not boil. Pour into glass and top with cream; serve hot.', N'irish_coffee.jpg', N'https://en.wikipedia.org/wiki/Irish_coffee')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (42, N'Kir', N'Add the crème de cassis to the bottom of the glass, then top up with wine.', N'kir.jpg', N'https://en.wikipedia.org/wiki/Kir_(cocktail)')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (43, N'Long Island Iced Tea', N'Add all ingredients into highball glass filled with ice. Stir gently. Garnish with lemon spiral. Serve with straw.', N'long_island_iced_tea.jpg', N'https://en.wikipedia.org/wiki/Long_Island_Iced_Tea')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (44, N'Mai Tai', N'Shake all ingredients with ice. Strain into glass. Garnish and serve with straw.', N'mai_tai.jpg', N'https://en.wikipedia.org/wiki/Mai_Tai')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (45, N'Margarita', N'Rub the rim of the glass with the lime slice to make the salt stick to it. Take care to moisten only the outer rim and sprinkle the salt on it. The salt should present to the lips of the imbiber and never mix into the cocktail. Shake the other ingredients with ice, then carefully pour into the glass.', N'margarita.jpg', N'https://en.wikipedia.org/wiki/Margarita')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (46, N'Mimosa', N'Ensure both ingredients are well chilled, then mix into the glass. Serve cold.', N'mimosa.jpg', N'https://en.wikipedia.org/wiki/Mimosa_(cocktail)')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (47, N'Mint julep', N'In a highball glass gently muddle the mint, sugar and water. Fill the glass with cracked ice, add Bourbon and stir well until the glass is well frosted. Garnish with a mint sprig.', N'mint_julep.jpg', N'https://en.wikipedia.org/wiki/Mint_julep')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (48, N'Mojito', N'Muddle mint leaves with sugar and lime juice. Add a splash of soda water and fill the glass with cracked ice. Pour the rum and top with soda water. Garnish and serve with straw.', N'mojito.jpg', N'https://en.wikipedia.org/wiki/Mojito')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (49, N'Moscow mule', N'Combine vodka and ginger beer in a highball glass filled with ice. Add lime juice. Stir gently. Garnish.', N'moscow_mule.jpg', N'https://en.wikipedia.org/wiki/Moscow_mule')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (50, N'Piña colada', N'Mix with crushed ice in blender until smooth. Pour into chilled glass, garnish and serve.', N'pina_colada.jpg', N'https://en.wikipedia.org/wiki/Pi%C3%B1a_colada')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (51, N'Rose', N'Shake together in a cocktail shaker, then strain into chilled glass. Garnish and serve.', N'rose.jpg', N'https://en.wikipedia.org/wiki/Rose_(cocktail)')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (52, N'Sea Breeze', N'Build all ingredients in a highball glass filled with ice. Garnish with lime wedge.', N'sea_breeze.jpg', N'https://en.wikipedia.org/wiki/Sea_Breeze_(cocktail)')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (53, N'Sex on the Beach', N'Build all ingredients in a highball glass filled with ice. Garnish with orange slice.', N'sex_on_the_beach.jpg', N'https://en.wikipedia.org/wiki/Sex_on_the_Beach')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (54, N'Singapore Sling', N'Pour all ingredients into cocktail shaker filled with ice cubes. Shake well. Strain into highball glass. Garnish with pineapple and cocktail cherry.', N'singapore_sling.jpg', N'https://en.wikipedia.org/wiki/Singapore_Sling')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (55, N'Tequila Sunrise', N'Pour the tequila and orange juice into glass over ice. Add the grenadine, which will sink to the bottom. Stir gently to create the sunrise effect. Garnish and serve.', N'tequila_sunrise.jpg', N'https://en.wikipedia.org/wiki/Tequila_Sunrise_(cocktail)')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (56, N'Bramble', N'Fill glass with crushed ice. Build gin, lemon juice and simple syrup over. Stir, and then pour blackberry liqueur over in a circular fashion to create marbling effect. Garnish with two blackberries and lemon slice.', N'bramble.jpg', N'https://en.wikipedia.org/wiki/Bramble_(cocktail)')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (57, N'B-52', N'Layer ingredients into a shot glass. Serve with a stirrer.', N'b52.jpg', N'https://en.wikipedia.org/wiki/B-52_(cocktail)')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (58, N'Dark ''N'' Stormy', N'In a highball glass filled with ice add 6cl dark rum and top with ginger beer. Garnish with lime wedge.', N'dark_n_stormy.jpg', N'https://en.wikipedia.org/wiki/Dark_''N''_Stormy')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (59, N'Dry Martini', N'Straight: Pour all ingredients into mixing glass with ice cubes. Stir well. Strain in chilled martini cocktail glass. Squeeze oil from lemon peel onto the drink, or garnish with olive.', N'dry_martini.jpg', N'https://en.wikipedia.org/wiki/Martini_(cocktail)')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (60, N'French Martini', N'Pour all ingredients into shaker with ice cubes. Shake well and strain into a chilled cocktail glass. Squeeze oil from lemon peel onto the drink.', NULL, N'https://en.wikipedia.org/wiki/French_Martini')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (61, N'Kamikaze', N'Shake all ingredients together with ice. Strain into glass, garnish and serve.', N'kamikaze.jpg', N'https://en.wikipedia.org/wiki/Kamikaze_(cocktail)')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (62, N'Lemon drop martini', N'Shake and strain into a chilled cocktail glass rimmed with sugar.', N'lemon_drop_martini.jpg', N'https://en.wikipedia.org/wiki/Lemon_Drop_Martini')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (63, N'Pisco sour', N'Vigorously shake and strain contents in a cocktail shaker with ice cubes, then pour into glass and garnish with bitters.', N'pisco_sour.jpg', N'https://en.wikipedia.org/wiki/Pisco_sour')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (64, N'Spritz Veneziano', N'Build into glass over ice, garnish and serve.', NULL, N'https://en.wikipedia.org/wiki/Spritz_(alcoholic_beverage)')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (65, N'Vesper', N'Shake over ice until well chilled, then strain into a deep goblet and garnish with a thin slice of lemon peel.', N'vesper.jpg', N'https://en.wikipedia.org/wiki/Vesper_(cocktail)')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (66, N'Yellow Bird', N'Shake and strain into a chilled cocktail glass', NULL, N'https://en.wikipedia.org/wiki/Yellow_Bird_(cocktail)')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (67, N'Gibson', N'Stir well in a shaker with ice, then strain into a chilled martini glass. Garnish and serve', N'gibson.jpg', N'https://en.wikipedia.org/wiki/Gibson_(cocktail)')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (68, N'Greyhound', N'Mix gin and juice, pour over ice in Old Fashioned glass', N'greyhound.jpg', N'https://en.wikipedia.org/wiki/Greyhound_(cocktail)')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (69, N'Bronx', N'Pour into cocktail shaker all ingredients with ice cubes, shake well. Strain in chilled cocktail or martini glass.', N'bronx.jpg', N'https://en.wikipedia.org/wiki/Bronx_(cocktail)')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (70, N'Rob Roy', N'Stirred over ice, strained into a chilled glass, garnished, and served straight up, or mixed in rocks glass, filled with ice.', N'rob_roy.jpg', N'https://en.wikipedia.org/wiki/Rob_Roy_(cocktail)')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (71, N'White Russian', N'Pour coffee liqueur and vodka into an Old Fashioned glass filled with ice. Float fresh cream on top and stir slowly.', N'white_russian.jpg', N'https://en.wikipedia.org/wiki/White_Russian_(cocktail)')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (72, N'Appletini', N'Mix in a shaker, then pour into a chilled glass. Garnish and serve.', N'appletini.jpg', N'https://en.wikipedia.org/wiki/Appletini')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (73, N'Hemingway Special', N'Pour all ingredients into a shaker with ice. Shake.', N'hemingway_special.jpg', N'https://en.wikipedia.org/wiki/Hemingway_Special')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (74, N'Barracuda', N'Shake pour ingredients with ice. Strain into glass, top with Sparkling wine.', NULL, N'https://en.wikipedia.org/wiki/Barracuda_(cocktail)')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (75, N'John Collins', N'Pour all ingredients directly into highball glass filled with ice. Stir gently. Garnish. Add a dash of Angostura bitters.', NULL, N'https://en.wikipedia.org/wiki/John_Collins_(cocktail)')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (76, N'Golden Cadillac', N'Shake together over ice. Strain into cocktail glass and serve chilled.', NULL, N'https://en.wikipedia.org/wiki/Golden_Cadillac')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (77, N'Donkey Punch', N'Pour all ingredients into shaker filled with ice. Shake well. Pour into large glass, filled with ice cubes. Garnish with orange slices.', NULL, N'https://en.wikipedia.org/wiki/Donkey_punch_(cocktail)')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (78, N'Japanese Slipper', N'Shake together in a mixer with ice. Strain into glass, garnish and serve.', N'japanese_slipper.jpg', N'https://en.wikipedia.org/wiki/Japanese_slipper')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (79, N'Vodka Martini', N'Straight: Pour all ingredients into mixing glass with ice cubes. Shake well. Strain in chilled martini cocktail glass. Squeeze oil from lemon peel onto the drink, or garnish with olive.', N'vodka_martini.jpg', N'https://en.wikipedia.org/wiki/Vodka_Martini')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (80, N'Buck''s Fizz', N'Pour the orange juice into glass and top up Champagne. Stir gently, garnish and serve.', N'bucks_fizz.jpg', N'https://en.wikipedia.org/wiki/Buck%27s_Fizz')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (81, N'Kir royal', N'Add the crème de cassis to the bottom of the glass, then top up champagne.', N'kir_royal.jpg', N'https://en.wikipedia.org/wiki/Kir_Royal')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (82, N'Redheaded slut', N'Combine Jägermeister and schnapps in glass full of ice. Add cranberry juice to fill to top. Stir as necessary.', NULL, N'https://en.wikipedia.org/wiki/Redheaded_slut')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (83, N'Agent Orange', N'Mix in an old fashioned glass with ice and serve.', NULL, N'https://en.wikipedia.org/wiki/Agent_Orange_(cocktail)')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (84, N'Russian Spring Punch', N'Pour the ingredients into an highball glass, top with Sparkling wine.', NULL, N'https://en.wikipedia.org/wiki/Russian_Spring_Punch')
INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (85, N'Don Alfredo', N'In a old-fashioned glass filled with ice, pour in Pisco, elderflower liqueur (such as St Germain) and lime juice. Stir and top with club soda.', N'don_alfredo.jpg', N'https://en.wikipedia.org/wiki/Don_Alfredo_(cocktail)')
