INSERT INTO [dbo].[Cocktail] ([ID], [Name], [Instructions], [Image], [WikipediaArticleUri]) VALUES (87, N'Bobby Burns', N'Put all liquid ingredients in cocktail shaker filled with ice. Squeeze lemon peel on top. Shake, strain into glass.', N'bobby_burns.jpg', N'https://en.wikipedia.org/wiki/Bobby_Burns_(drink)')
